INSERT INTO `tbl_product` (`id`, `id_category`, `id_brand`, `name`, `code`, `price`, `promotional_price`, `quantity`, `status`, `description`, `screen`, `ram`, `cpu`, `memory`, `operating_system`, `front_camera`, `rear_camera`, `user`, `create_date`, `image`, `level`) VALUES
(1, 13, 1, 'iPhone 15 Pro Max', 'IPHONE#01', 34990000, 33990000, 100, 'còn hàng', '<h2>iPhone 15 Pro Max sở hữu màn hình Super Retina XDR OLED 6.7 inch tần số quét cao 120Hz sắc nét và mượt mà. Với chipset A17 Pro sản xuất trên tiến trình 3nm, thế hệ iPhone 15 bản Pro Max đảm bảo hiệu năng mạnh mẽ và khả năng tiết kiệm pin tối ưu. Đặc biệt, máy còn sở hữu camera chính 48MP cùng khả năng zoom quang học 5x, giúp iP 15 Pro Max trở thành lựa chọn tuyệt vời cho người đam mê nhiếp ảnh và quay phim chuyên nghiệp.</h2>', '6.7 inch OLED', '8GB', 'Apple A17 Pro', '256GB', 'iOS 17', '12MP', '48MP + 12MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://cdn2.cellphones.com.vn/insecure/rs:fill:0:358/q:90/plain/https://cellphones.com.vn/media/catalog/product/i/p/iphone-15-pro-max_3.png', 'hot'),
(2, 13, 1, 'iPhone 15 Pro', 'IPHONE#02', 31990000, 30990000, 200, 'còn hàng', '<h2>Điện thoại iPhone 15 Pro được trang bị màn hình mới Dynamic Island với kích thước 6.1 inch cùng công nghệ ProMotion và tính năng Always On Display. Thiết bị cũng có nhiều thay đổi trong thiết kế với nút hành động cùng cổng sạc USB-C, khung viền titan sang trọng. Thế hệ điện thoại iPhone 15 mới này được trang bị 04 màu sắc mới như Titan tự nhiên, titan đen, titan trắng và titan xanh.</h2>', '6.1 inch OLED', '8GB', 'Apple A17 Pro', '256GB', 'iOS 17', '12MP', '48MP + 12MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://halomobile.vn/wp-content/uploads/2023/09/iphone-15-pro-xanh-halo.png', 'hot'),
(3, 13, 1, 'iPhone 15 Plus', 'IPHONE#03', 27990000, 26990000, 150, 'còn hàng', '<h2>iPhone 15 Plus được đánh giá cao bởi màn hình Dynamic Island 6.7 inch với mặt kính lưng pha màu ấn tượng, chip A16 Bionic mạnh mẽ cùng cổng sạc USB-C cho khả năng sạc đầy nhanh chóng. Ngoài ra, phiên bản Plus thuộc series iPhone 15 còn sở hữu 5 phiên bản màu pastel thanh lịch phù hợp với nhiều đối tượng khách hàng: hồng, vàng, xanh lá, xanh dương, đen.</h2>', '6.7 inch OLED', '6GB', 'Apple A16', '128GB', 'iOS 17', '12MP', '48MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://cdn.tgdd.vn/Products/Images/42/303891/iphone-15-plus-128gb-xanh-thumb-600x600.jpg', 'hot'),
(4, 13, 1, 'iPhone 15', 'IPHONE#04', 24990000, 23990000, 200, 'còn hàng', '<h2>iPhone 15 128GB được trang bị màn hình Dynamic Island kích thước 6.1 inch với công nghệ hiển thị Super Retina XDR màn lại trải nghiệm hình ảnh vượt trội. Điện thoại với mặt lưng kính nhám chống bám mồ hôi cùng 5 phiên bản màu sắc lựa chọn: Hồng, Vàng, Xanh lá, Xanh dương và đen. Camera trên iPhone 15 series cũng được nâng cấp lên cảm biến 48MP cùng tính năng chụp zoom quang học tới 2x. Cùng với thiết kế cổng sạc thay đổi từ lightning sang USB-C vô cùng ấn tượng.</h2>', '6.1 inch OLED', '6GB', 'Apple A16', '128GB', 'iOS 17', '12MP', '48MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://cdn2.cellphones.com.vn/insecure/rs:fill:358:358/q:90/plain/https://cellphones.com.vn/media/catalog/product/i/p/iphone-15-plus_1__1.png', 'hot'),
(5, 13, 1, 'iPhone 14 Pro Max', 'IPHONE#05', 29990000, 28990000, 300, 'còn hàng', '<h2>iPhone 14 Pro Max gây ấn tượng mạnh với thiết kế sang trọng, Dynamic Island thông minh và màn hình LTPO 120Hz siêu mượt. Chip A16 Bionic cho hiệu năng vượt trội, hỗ trợ quay video chuyên nghiệp cùng camera 48MP. Đây là mẫu máy lý tưởng cho người dùng yêu cầu cao về hiệu năng và camera.</h2>', '6.7 inch OLED', '6GB', 'Apple A16', '256GB', 'iOS 16', '12MP', '48MP + 12MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://cdn2.cellphones.com.vn/insecure/rs:fill:0:358/q:90/plain/https://cellphones.com.vn/media/catalog/product/x/_/x_m_24.png', 'hot'),
(6, 13, 1, 'iPhone 14', 'IPHONE#06', 20990000, 19990000, 400, 'còn hàng', '<h2>iPhone 14 giữ nguyên thiết kế quen thuộc nhưng cải tiến ở khả năng chụp ảnh và hiệu năng. Với chip A15 Bionic và công nghệ Photonic Engine, ảnh chụp trong điều kiện thiếu sáng rõ nét hơn. Máy nhẹ, dễ cầm, rất phù hợp cho người dùng phổ thông hoặc nâng cấp từ các đời iPhone cũ.</h2>', '6.1 inch OLED', '6GB', 'Apple A15', '128GB', 'iOS 16', '12MP', '12MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://cdn2.cellphones.com.vn/insecure/rs:fill:358:358/q:90/plain/https://cellphones.com.vn/media/catalog/product/i/p/iphone-14-plus_1__2.png', 'hot'),
(7, 13, 1, 'iPhone 13', 'IPHONE#07', 17990000, 16990000, 500, 'còn hàng', '<h2>iPhone 13 thường được trang bị chip A15 Bionic mạnh mẽ với 6 nhân CPU và 4 nhân GPU, cung cấp mức hiệu năng vượt trội, giúp xử lý nhanh chóng các tác vụ nặng. Màn hình Super Retina XDR 6.1 inch trên máy cũng được đánh giá cao khi mang tới hình ảnh sắc nét với độ sáng cao, tối ưu hóa trải nghiệm xem nội dung dưới mọi điều kiện ánh sáng. Chưa hết, iPhone13 còn sở hữu hệ thống camera kép 12MP với công nghệ ổn định hình ảnh quang học (OIS) cải thiện khả năng quay film, chụp hình, ngay cả khi đang ở trong môi trường ánh sáng yếu.</h2>', '6.1 inch OLED', '4GB', 'Apple A15', '128GB', 'iOS 15', '12MP', '12MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-midnight-2-600x600.jpg', 'hot'),
(8, 13, 1, 'iPhone 13 mini', 'IPHONE#08', 16990000, 15990000, 300, 'còn hàng', '<h2>Apple vừa trình làng iPhone 13 mini cùng với 3 phiên bản khác là iPhone 13 Pro và 13 Pro Max cùng iPhone 13 thường tương tự năm ngoái. Tuy nhiên dòng sản phẩm mới năm nay có nhiều nâng cấp về cấu hình, camera, thiết kế lẫn màu sắc. Như vậy, Apple đã không thay đổi số lượng phiên bản cũng như kích thước màn hình của loạt điện thoại iPhone 13 năm nay. </h2>', '5.4 inch OLED', '4GB', 'Apple A15', '128GB', 'iOS 15', '12MP', '12MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://cdn.phuckhangmobile.com/image/iphone-13-hong-900-24965j.jpg', 'hot'),
(9, 13, 1, 'iPhone SE 2022', 'IPHONE#09', 10990000, 9990000, 400, 'còn hàng', '<h2>iPhone SE 2022 đã ghi dấu ấn với thiết kế nhỏ gọn và hiệu năng mạnh mẽ trong phân khúc giá rẻ. Giá rẻ, chip mạnh, thiết kế cổ điển, mang đến làn gió mới cho người dùng yêu thích dòng iPhone giá phải chăng.</h2>', '4.7 inch LCD', '4GB', 'Apple A15', '64GB', 'iOS 15', '7MP', '12MP', 'admin', '2025-06-23 11:56:26', 'https://bizweb.dktcdn.net/100/401/951/products/dac-diem-noi-bat-2392dc24-8398-4f1b-8321-32202c44da29.png?v=1729506920280', 'hot'),
(10, 13, 1, 'iPhone 12', 'IPHONE#10', 15990000, 14990000, 500, 'còn hàng', '<h2>iPhone 12 là dòng máy đánh dấu sự trở lại của thiết kế cạnh phẳng hiện đại. Trang bị chip A14 Bionic và màn hình OLED chất lượng cao, iPhone 12 vẫn đáp ứng tốt mọi tác vụ năm 2025. Máy hỗ trợ 5G, chống nước IP68 và camera chụp đẹp không kém đời mới.</h2>', '6.1 inch OLED', '4GB', 'Apple A14', '128GB', 'iOS 14', '12MP', '12MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-tim-1-600x600.jpg', 'hot'),
(11, 13, 2, 'Samsung Galaxy S23 Ultra', 'SAMSUNG#01', 31990000, 29990000, 500, 'còn hàng', '<h2>Samsung Galaxy S23 Ultra là flagship hội tụ công nghệ cao nhất của Samsung, với camera chính 200MP, bút S-Pen tích hợp và chip Snapdragon 8 Gen 2 cho Galaxy tối ưu mạnh mẽ. Màn hình AMOLED 6.8 inch 120Hz siêu mượt, pin 5000mAh và khả năng zoom lên đến 100x giúp S23 Ultra trở thành lựa chọn hàng đầu cho người yêu công nghệ và nhiếp ảnh.</h2>', '6.8 inch AMOLED', '12GB', 'Snapdragon 8 Gen 2', '256GB', 'Android 13', '12MP', '200MP + 12MP + 10MP', 'admin', '2025-06-23 11:56:26', 'https://galaxydidong.vn/wp-content/uploads/2023/02/galaxydidong-s23-ultra-tim-nhat.jpg', 'hot'),
(12, 13, 2, 'Samsung Galaxy S23', 'SAMSUNG#02', 21990000, 19990000, 500, 'còn hàng', '<h2>Samsung Galaxy S23 là mẫu flagship nhỏ gọn nhưng cực kỳ mạnh mẽ, sở hữu chip Snapdragon 8 Gen 2 cùng cụm camera sau 50MP chất lượng cao. Với thiết kế sang trọng, màn hình Dynamic AMOLED 120Hz mượt mà, đây là chiếc điện thoại lý tưởng cho người dùng yêu thích sự cân đối giữa hiệu năng và sự tinh tế.</h2>', '6.1 inch AMOLED', '8GB', 'Snapdragon 8 Gen 2', '128GB', 'Android 13', '12MP', '50MP + 12MP + 10MP', 'admin', '2025-06-23 11:56:26', 'https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-600x600.jpg', 'hot'),
(13, 13, 2, 'Samsung Galaxy Z Fold4', 'SAMSUNG#03', 40990000, 37990000, 200, 'còn hàng', '<h2>Fold4 đánh dấu bước tiến lớn trong công nghệ màn hình gập. Với thiết kế mở ra như một chiếc tablet và cấu hình flagship từ Snapdragon 8+ Gen 1, máy hỗ trợ đa nhiệm tuyệt vời, chụp ảnh linh hoạt và trải nghiệm giải trí vượt trội. Sản phẩm lý tưởng cho người dùng doanh nhân, sáng tạo nội dung hoặc yêu thích công nghệ độc đáo.</h2>', '7.6 inch AMOLED', '12GB', 'Snapdragon 8+ Gen 1', '256GB', 'Android 12L', '10MP', '50MP + 12MP + 10MP', 'admin', '2025-06-23 11:56:26', 'https://onewaymobile.vn/images/products/2022/09/21/original/z-fold-4-ow-3_1663728219-copy.png', 'hot'),
(14, 13, 2, 'Samsung Galaxy Z Flip4', 'SAMSUNG#04', 23990000, 21990000, 300, 'còn hàng', '<h2>Z Flip4 là lựa chọn hoàn hảo cho người dùng yêu thích thời trang và sự tiện lợi. Khi gập lại, máy nhỏ gọn như một hộp phấn. Khi mở ra, màn hình AMOLED 6.7 inch sắc nét cho trải nghiệm sử dụng trọn vẹn. Sở hữu chip Snapdragon 8+ Gen 1, pin bền và sạc nhanh, đây là smartphone “thời trang công nghệ” đúng nghĩa.</h2>', '6.7 inch AMOLED', '8GB', 'Snapdragon 8+ Gen 1', '128GB', 'Android 12', '10MP', '12MP + 12MP', 'admin', '2025-06-23 11:56:26', 'https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-5g-128gb-thumb-tim-600x600.jpg', 'hot'),
(15, 13, 2, 'Samsung Galaxy A73', 'SAMSUNG#05', 11990000, 9990000, 600, 'còn hàng', '<h2>Galaxy A73 nổi bật với camera 108MP cùng màn hình Super AMOLED+ 6.7 inch 120Hz mượt mà. Trang bị Snapdragon 778G và viên pin 5000mAh, A73 vừa mạnh vừa bền, lý tưởng cho người dùng yêu cầu cao về cả giải trí lẫn công việc trong phân khúc cận cao cấp.</h2>', '6.7 inch AMOLED', '8GB', 'Snapdragon 778G', '128GB', 'Android 12', '32MP', '108MP + 12MP + 5MP + 5MP', 'admin', '2025-06-23 11:56:26', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSk1d-wX1Ckg92hPDr9WK-wYmwTp8nJ9d1YTw&s', 'hot'),
(16, 13, 2, 'Samsung Galaxy A54', 'SAMSUNG#06', 10490000, 9490000, 700, 'còn hàng', '<h2>Galaxy A54 sở hữu thiết kế hiện đại lấy cảm hứng từ dòng S cao cấp, cùng chip Exynos 1380 mạnh mẽ. Màn hình AMOLED 120Hz siêu mượt, camera chống rung OIS và viên pin 5000mAh giúp máy hoạt động mượt mà suốt ngày dài. Rất phù hợp cho người dùng trẻ năng động.</h2>', '6.4 inch AMOLED', '8GB', 'Exynos 1380', '128GB', 'Android 13', '32MP', '50MP + 12MP + 5MP', 'admin', '2025-06-23 11:56:26', 'https://cdn2.cellphones.com.vn/insecure/rs:fill:358:358/q:90/plain/https://cellphones.com.vn/media/catalog/product/s/m/sm-a546_galaxy_a54_5g_awesome_violet_front_4_3_1_1.png', 'hot'),
(17, 13, 2, 'Samsung Galaxy A34', 'SAMSUNG#07', 8490000, 7990000, 500, 'còn hàng', '<h2>Galaxy A34 mang lại trải nghiệm mượt mà nhờ màn hình AMOLED 120Hz và chip Dimensity 1080 hiệu năng tốt. Camera 48MP hỗ trợ chống rung OIS, pin 5000mAh cùng thiết kế thời trang khiến A34 nổi bật trong phân khúc tầm trung.</h2>', '6.6 inch AMOLED', '6GB', 'Dimensity 1080', '128GB', 'Android 13', '13MP', '48MP + 8MP + 5MP', 'admin', '2025-06-23 11:56:26', 'https://didongmango.com/images/products/2023/09/26/large/galaxy-a34-5g-bac_6-copy_1695784873.jpg', 'hot'),
(18, 13, 2, 'Samsung Galaxy M14', 'SAMSUNG#08', 4490000, 3990000, 800, 'còn hàng', '<h2>Galaxy M14 nổi bật với viên pin khủng 6000mAh, phù hợp cho người dùng cần sử dụng dài ngày. Máy trang bị chip Exynos 1330 mạnh mẽ, màn hình lớn 6.6 inch, hỗ trợ 5G và giá cực kỳ hấp dẫn trong phân khúc giá rẻ.</h2>', '6.6 inch PLS LCD', '4GB', 'Exynos 1330', '64GB', 'Android 13', '13MP', '50MP + 2MP + 2MP', 'admin', '2025-06-23 11:56:26', 'https://cdn.tgdd.vn/Products/Images/42/298539/samsung-galaxy-m14-5g-050723-105705-600x600.jpg', 'hot'),
(19, 13, 2, 'Samsung Galaxy A14', 'SAMSUNG#09', 3990000, 3590000, 900, 'còn hàng', '<h2>Galaxy A14 là mẫu smartphone phổ thông nổi bật nhờ thiết kế trẻ trung và cấu hình ổn định. Với chip Exynos 850, camera 50MP, màn hình lớn 6.6 inch cùng pin 5000mAh, máy phục vụ tốt nhu cầu học tập, làm việc và giải trí nhẹ nhàng.</h2>', '6.6 inch PLS LCD', '4GB', 'Exynos 850', '64GB', 'Android 13', '13MP', '50MP + 5MP + 2MP', 'admin', '2025-06-23 11:56:26', 'https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-thumb-nau-600x600.jpg', 'hot'),
(20, 13, 2, 'Samsung Galaxy A04s', 'SAMSUNG#10', 3190000, 2990000, 1000, 'còn hàng', '<h2>Galaxy A04s là điện thoại giá rẻ nhưng sở hữu camera 50MP ấn tượng, pin lớn 5000mAh và màn hình 6.5 inch mượt mà. Máy phù hợp cho học sinh, sinh viên hoặc người dùng cần thiết bị ổn định để liên lạc, học online, xem video.</h2>', '6.5 inch PLS LCD', '4GB', 'Exynos 850', '64GB', 'Android 12', '5MP', '50MP + 2MP + 2MP', 'admin', '2025-06-23 11:56:26', 'https://cdn.tgdd.vn/Products/Images/42/283819/samsung-galaxy-a04s-thumb-den-600x600.jpg', 'hot'),
(21, 13, 3, 'Xiaomi 13 Ultra', 'XIAOMI#01', 26990000, 24990000, 300, 'còn hàng', '<h2>Xiaomi 13 Ultra là siêu phẩm camera với sự hợp tác giữa Xiaomi và Leica. Cụm camera 50MP với cảm biến 1 inch mang lại chất lượng ảnh cực kỳ ấn tượng trong mọi điều kiện ánh sáng. Kết hợp chip Snapdragon 8 Gen 2 và màn hình AMOLED WQHD+ 120Hz, thiết bị này không chỉ mạnh mẽ mà còn hoàn hảo về trải nghiệm hình ảnh.</h2>', '6.73 inch AMOLED', '12GB', 'Snapdragon 8 Gen 2', '512GB', 'Android 13', '32MP', '50MP + 50MP + 50MP + 50MP', 'admin', '2025-06-23 11:57:37', 'https://www.giztop.com/media/catalog/product/cache/97cc1143d2e20f2b0c8ea91aaa12053c/x/i/xiaomi_13_ultra_black.jpg', 'hot'),
(22, 13, 3, 'Xiaomi 13 Pro', 'XIAOMI#02', 19990000, 18990000, 400, 'còn hàng', '<h2>Với mặt lưng gốm sang trọng và cụm camera Leica cao cấp, Xiaomi 13 Pro là lựa chọn lý tưởng cho người yêu nhiếp ảnh. Thiết bị được trang bị màn hình AMOLED 2K, tần số quét 120Hz, chip Snapdragon 8 Gen 2 và sạc nhanh 120W, giúp tái định nghĩa sự cao cấp trong phân khúc Android.</h2>', '6.73 inch AMOLED', '12GB', 'Snapdragon 8 Gen 2', '256GB', 'Android 13', '32MP', '50MP + 50MP + 50MP', 'admin', '2025-06-23 11:57:37', 'https://cdn.tgdd.vn/Products/Images/42/282903/xiaomi-13-pro-thumb-1-2-600x600.jpg', 'hot'),
(23, 13, 3, 'Xiaomi 13', 'XIAOMI#03', 16990000, 15990000, 300, 'còn hàng', '<h2>Gọn nhẹ nhưng đầy uy lực, Xiaomi 13 mang trong mình sức mạnh từ Snapdragon 8 Gen 2 cùng cụm camera chất lượng Leica. Với thiết kế tinh tế, màn hình OLED 120Hz sống động và hiệu năng vượt trội, đây là chiếc flagship nhỏ gọn đáng giá cho người dùng yêu công nghệ.</h2>', '6.36 inch AMOLED', '8GB', 'Snapdragon 8 Gen 2', '128GB', 'Android 13', '32MP', '50MP + 10MP + 12MP', 'admin', '2025-06-23 11:57:37', 'https://happyphone.vn/wp-content/uploads/2024/10/DTDD-Xiaomi-Redmi-13-6GB-128GB-Hong.jpg', 'hot'),
(24, 13, 3, 'Xiaomi 12T Pro', 'XIAOMI#04', 13990000, 12990000, 500, 'còn hàng', '<h2>Chiếc điện thoại tiên phong với camera 200MP, Xiaomi 12T Pro là sự kết hợp giữa thiết kế thanh lịch và hiệu năng cực mạnh. Máy dùng Snapdragon 8+ Gen 1, sạc siêu nhanh 120W và màn hình AMOLED sắc nét – phù hợp cho cả game thủ lẫn người yêu nhiếp ảnh.</h2>', '6.67 inch AMOLED', '8GB', 'Snapdragon 8+ Gen 1', '256GB', 'Android 12', '20MP', '200MP + 8MP + 2MP', 'admin', '2025-06-23 11:57:37', 'https://cdn.tgdd.vn/Products/Images/42/279066/xiaomi-12t-pro-thumb-bac-1-600x600.jpg', 'hot'),
(25, 13, 3, 'Xiaomi 12', 'XIAOMI#05', 11990000, 10990000, 500, 'còn hàng', '<h2>Xiaomi 12 là một flagship gọn nhẹ với hiệu năng mạnh mẽ từ Snapdragon 8 Gen 1. Thiết kế hiện đại, camera 50MP chống rung OIS, màn hình AMOLED rực rỡ và khả năng sạc nhanh 67W giúp người dùng tận hưởng trải nghiệm cao cấp với giá cả hợp lý.</h2>', '6.28 inch AMOLED', '8GB', 'Snapdragon 8 Gen 1', '128GB', 'Android 12', '32MP', '50MP + 13MP + 5MP', 'admin', '2025-06-23 11:57:37', 'https://cdn.tgdd.vn/Products/Images/42/234621/Xiaomi-12-xam-thumb-mau-600x600.jpg', 'hot'),
(26, 13, 3, 'Redmi Note 12 Pro+', 'XIAOMI#06', 9490000, 8990000, 600, 'còn hàng', '<h2>Redmi Note 12 Pro+ gây sốc với camera 200MP trong phân khúc dưới 10 triệu. Trang bị chip Dimensity 1080, màn hình AMOLED 120Hz và sạc nhanh 120W, chiếc máy này là minh chứng cho việc bạn không cần bỏ ra số tiền lớn để có trải nghiệm cao cấp.</h2>', '6.67 inch AMOLED', '8GB', 'Dimensity 1080', '256GB', 'Android 12', '16MP', '200MP + 8MP + 2MP', 'admin', '2025-06-23 11:57:37', 'https://cdn.tgdd.vn/Products/Images/42/278887/xiaomi-redmi-note-12-pro-plus-5g-1-600x600.jpg', 'hot'),
(27, 13, 3, 'Redmi Note 12 Pro', 'XIAOMI#07', 7990000, 7490000, 700, 'còn hàng', '<h2>Redmi Note 12 Pro sở hữu camera chính 50MP hỗ trợ chống rung OIS, chip Dimensity 1080 và màn hình AMOLED 120Hz sống động. Thiết kế tinh tế, hiệu năng ổn định và khả năng chụp ảnh đẹp khiến đây là một trong những chiếc điện thoại tầm trung đáng mua nhất.</h2>', '6.67 inch AMOLED', '8GB', 'Dimensity 1080', '128GB', 'Android 12', '16MP', '50MP + 8MP + 2MP', 'admin', '2025-06-23 11:57:37', 'https://cdn.tgdd.vn/Products/Images/42/299734/xiaomi-redmi-12-pro-4g-xanh-thumb-600x600.jpg', 'hot'),
(28, 13, 3, 'Redmi Note 12', 'XIAOMI#08', 4990000, 4590000, 800, 'còn hàng', '<h2>Một chiếc smartphone giá rẻ với màn hình AMOLED 120Hz hiếm thấy trong phân khúc. Redmi Note 12 dùng chip Snapdragon 685, camera 50MP và pin 5000mAh, mang đến trải nghiệm mượt mà và bền bỉ, đặc biệt phù hợp cho sinh viên và người dùng cơ bản.</h2>', '6.67 inch AMOLED', '6GB', 'Snapdragon 685', '128GB', 'Android 13', '13MP', '50MP + 8MP + 2MP', 'admin', '2025-06-23 11:57:37', 'https://cdn.tgdd.vn/Products/Images/42/303298/xiaomi-redmi-note-12-vang-1-thumb-momo-600x600.jpg', 'hot'),
(29, 13, 3, 'POCO X5 Pro', 'XIAOMI#09', 7990000, 7490000, 500, 'còn hàng', '<h2>POCO X5 Pro là chiếc smartphone gaming tầm trung với chip Snapdragon 778G mạnh mẽ, màn hình AMOLED 120Hz và camera 108MP. Thiết kế trẻ trung, pin bền và sạc nhanh giúp bạn chiến game cả ngày không lo hết pin.</h2>', '6.67 inch AMOLED', '6GB', 'Snapdragon 778G', '128GB', 'Android 12', '16MP', '108MP + 8MP + 2MP', 'admin', '2025-06-23 11:57:37', 'https://cdn.viettablet.com/images/thumbnails/480/516/detailed/59/xiaomi-poco-x5-pro.png', 'hot'),
(30, 13, 3, 'Redmi 12C', 'XIAOMI#10', 2990000, 2690000, 1000, 'còn hàng', '<h2>Redmi 12C là lựa chọn lý tưởng cho người cần một thiết bị giá rẻ nhưng ổn định. Với chip Helio G85, camera 50MP và pin 5000mAh, chiếc máy này đáp ứng tốt các nhu cầu cơ bản như gọi điện, nhắn tin, học online và xem video nhẹ nhàng.</h2>', '6.71 inch IPS LCD', '4GB', 'Helio G85', '64GB', 'Android 12', '5MP', '50MP + 0.08MP', 'admin', '2025-06-23 11:57:37', 'https://cdn.tgdd.vn/Products/Images/42/303163/xiaomi-redmi-12c-grey-thumb-600x600.jpg', 'hot'),
(31, 13, 4, 'Oppo Find X6 Pro', 'OPPO#01', 27990000, 25990000, 300, 'còn hàng', '<h2>Oppo Find X6 Pro nổi bật với cụm camera siêu lớn, cảm biến 1 inch Sony IMX989 và công nghệ xử lý ảnh Hasselblad cao cấp. Chip Snapdragon 8 Gen 2, màn hình AMOLED 2K 120Hz và thiết kế da cao cấp tạo nên một thiết bị flagship đỉnh cao cả về hiệu năng lẫn trải nghiệm thị giác.</h2>', '6.82 inch AMOLED', '12GB', 'Dimensity 9200', '256GB', 'Android 13', '32MP', '50MP + 50MP + 50MP', 'admin', '2025-06-23 11:57:37', 'https://cdn.tgdd.vn/Products/Images/42/297050/oppo-find-x6-pro-1-600x600.jpg', 'hot'),
